<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/adminclient/MU3H/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/** * Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';require_once 'includes/auth.php';require_once 'Zend/Session.php';$zfsession = new Zend_Session_Namespace('client');/* Check if there is already one setup first. */if (!isset($zfsession->userData) || is_null($zfsession->userData) || empty($zfsession->userData)) { 	header('Location: /admin/login.php');	exit;} else {	/* Check if current page is allowed. */	//used for selected menu items	$page = explode('/',$_SERVER['REQUEST_URI']);	$currentPage = isset($page[2]) && trim($page[2]) != '' ? trim($page[2]) : '';	if($currentPage != '' && $currentPage != 'logout.php') {		if(!in_array($currentPage, $zfsession->domainData['pages'])) {			header('Location: /admin/login.php');			exit;		}	}	}$smarty->assign('userData', $zfsession->userData);?>