<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/* standard config include. */require_once 'config/database.php';require_once 'config/smarty.php';//include the Zend class for Authentificationrequire_once 'Zend/Session.php';// Set up the namespace$zfsession	= new Zend_Session_Namespace('ADMIN_CLIENT');	/* Check if there is already one setup first. */if (!isset($zfsession->domainData) || is_null($zfsession->domainData) || count($zfsession->domainData) == 0) { 	require_once 'class/campaign.php';	/* Get domain details. */	$campaignObject = new class_campaign();			/* Check if domain exists on our side. */	if(is_null($zfsession->domainData) || count($zfsession->domainData) == 0) {			header('Location: http://www.nolali.co.za/');		exit();					}	}// Check if logged in, otherwise redirectif (!isset($zfsession->identity) || is_null($zfsession->identity) || $zfsession->identity == '') {	header('Location: /login.php');	exit();	} else {	//instantiate the users class	require_once 'class/administrator.php';		$administratorObject = new class_administrator();		//get user details by username	$administratorData = $administratorObject->getByCode($zfsession->identity);		$smarty->assign('administratorData', $administratorData);}$smarty->assign('domainData', $zfsession->domainData);global $zfsession;?>