<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/** * Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';require_once 'includes/auth.php';require_once 'class/inquiry.php';$inquiryObject = new class_inquiry();if(isset($_GET['action']) && trim($_GET['action']) == 'tablesearch') {	$search = isset($_REQUEST['search']) && trim($_REQUEST['search']) != '' ? trim($_REQUEST['search']) : null;	$start 	= isset($_REQUEST['iDisplayStart']) ? $_REQUEST['iDisplayStart'] : 0;	$length = isset($_REQUEST['iDisplayLength']) ? $_REQUEST['iDisplayLength'] : 20;		$invoiceData = $inquiryObject->getSearch($search, $start, $length);	$all = array();	if($invoiceData) {		for($i = 0; $i < count($invoiceData['records']); $i++) {			$item = $invoiceData['records'][$i];			$all[$i] = array( 					$item['inquiry_added'],										$item['inquiry_name'],					$item['inquiry_email'],					$item['inquiry_number'],					$item['inquiry_subject'],					"<button onclick=\"viewModal('".$item['inquiry_subject']."', '".$item['inquiry_comments']."'); return false;\" class='btn btn-danger'>View</button>");		}	}		$response['sEcho'] = $_REQUEST['sEcho'];	$response['iTotalRecords'] = $invoiceData['displayrecords'];			$response['iTotalDisplayRecords'] = $invoiceData['count'];	$response['aaData']	= $all;	    echo json_encode($response);    die();	}/* Display the template */	$smarty->display('inquiry/default.tpl');?>