<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/** * Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';require_once 'Zend/Session.php';$zfsession = new Zend_Session_Namespace('ADMIN_CLIENT');require_once 'class/administrator.php';$administratorObject	= new class_administrator();if (!empty($_POST) && count($_POST) > 0) {	if(isset($_POST['username']) && isset($_POST['password'])) {			$username = (isset($_POST['username'])) ? $_POST['username'] : null;		$password = (isset($_POST['password'])) ? $_POST['password'] : null;				$administratorData	= $administratorObject->checkLogin($username, $password);		$message			= '';				if($administratorData) {					// Identity exists; store in session			$zfsession->administrator	= $administratorData;			$zfsession->identity			= $zfsession->administrator['administrator_code'];						//session_write_close();			header("Location: /");			exit;					}else{			$message = 'You are not a valid system user';		}	} else {		$message = 'Please put in username and password';	}	$smarty->assign('loginmessage', $message);}$smarty->assign('domainData', $zfsession->domainData);/* Display the template */	$smarty->display('login.tpl');?>