<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/** * Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';require_once 'includes/auth.php';require_once 'class/participant.php';$participantObject = new class_participant();if(isset($_GET['delete_code'])) {	$errorArray				= array();	$errorArray['error']	= '';	$errorArray['result']	= 0;		$formValid				= true;	$success				= NULL;	$code					= trim($_GET['delete_code']);	if($errorArray['error']  == '' && $errorArray['result']  == 0 ) {			$data	= array();		$data['participant_deleted'] 	= 1;				$data['participant_code']		= $code;						$success	= $participantObject->updateParticipant($data, 'EMAIL');				if(is_numeric($code) && $code > 0) {			$errorArray['error']	= '';			$errorArray['result']	= 1;					} else {			$errorArray['error']	= 'Could not delete, please try again.';			$errorArray['result']	= 0;						}	}	echo json_encode($errorArray);	exit;}if(isset($_GET['action']) && trim($_GET['action']) == 'tablesearch') {	$search = isset($_REQUEST['search']) && trim($_REQUEST['search']) != '' ? trim($_REQUEST['search']) : null;	$start 	= isset($_REQUEST['iDisplayStart']) ? $_REQUEST['iDisplayStart'] : 0;	$length = isset($_REQUEST['iDisplayLength']) ? $_REQUEST['iDisplayLength'] : 20;		$participantData = $participantObject->getSearch($search, $start, $length);	$all = array();	if($participantData) {		for($i = 0; $i < count($participantData['records']); $i++) {			$item = $participantData['records'][$i];			$all[$i] = array(					($item['participant_image_name'] == '' ? '<img src="/images/no-image.jpg" width="80" />' : '<img src="http://'.$item['campaign_domain'].'/'.$item['participant_image_path'].'/tny_'.$item['participant_image_name'].$item['participant_image_extension'].'" width="80" />'),					$item['participant_added'],					'<a class="'.($item['participant_active'] == 0 ? 'error' : 'success').'" href="/people/details.php?code='.$item['participant_code'].'">'.$item['participant_name'].' '.$item['participant_surname'].'</a>', 					$item['participant_email'], 					$item['participant_cellphone'],					($item['participant_active'] == 0 ? '<span class="error">Nonactive</span>' : '<span class="success">Active</span>'), 					"<button onclick=\"deleteModal('".$item['participant_code']."', '', 'default'); return false;\" class='btn btn-danger'>Delete</button>");		}	}		$response['sEcho'] = $_REQUEST['sEcho'];	$response['iTotalRecords'] = $participantData['displayrecords'];			$response['iTotalDisplayRecords'] = $participantData['count'];	$response['aaData']	= $all;	    echo json_encode($response);    die();	}/* Display the template */	$smarty->display('people/default.tpl');?>